import React, { Component } from 'react';

const keys = require('../../config/keys')

var GoogleAuth; // Google Auth object.
var isAuthorized;
var currentApiRequest;

class YouTubeAuth extends Component {
  constructor() {
    super();

    this.state = {
      credentials: null,
    }
  }

  initClient = () => {
    gapi.client.init({
        'apiKey': keys.GOOGLE_API_KEY,
        'clientId': key.GOOGLE_CLIENT_ID,
        'scope': key.GOOGLE_SCOPES,
        'discoveryDocs': ['https://www.googleapis.com/discovery/v1/apis/youtubeAnalytics/v1/rest']
    }).then(function () {
        GoogleAuth = gapi.auth2.getAuthInstance();

        // Listen for sign-in state changes.
        GoogleAuth.isSignedIn.listen(updateSigninStatus);
    });
  }

  /**
   * Store the request details. Then check to determine whether the user
   * has authorized the application.
   *   - If the user has granted access, make the API request.
   *   - If the user has not granted access, initiate the sign-in flow.
   */
  sendAuthorizedApiRequest = (requestDetails) => {
    currentApiRequest = requestDetails;
    if (isAuthorized) {
      // Make API request
      // gapi.client.request(requestDetails)

      // Reset currentApiRequest variable.
      currentApiRequest = {};
    } else {
      GoogleAuth.signIn();
    }
  }

  /**
   * Listener called when user completes auth flow. If the currentApiRequest
   * variable is set, then the user was prompted to authorize the application
   * before the request executed. In that case, proceed with that API request.
   */
  updateSigninStatus = (isSignedIn) => {
    if (isSignedIn) {
      isAuthorized = true;
      if (currentApiRequest) {
        this.sendAuthorizedApiRequest(currentApiRequest);
      }
    } else {
      isAuthorized = false;
    }
  }

  render() {
      return (
        <div>
          <button onClick={this.authenticate}>LOGIN</button>
        </div>
      );
    }
  }

export default YouTubeAuth;
