import React, { Component } from 'react';
import axios from 'axios';

const keys = require('../../config/keys');
var Content;

class YouTubeData extends Component {

  GetDates = () => {
    // getting current date
    var today = new Date();
    let dd = today.getDate();
    let mm = today.getMonth()+1;
    let yyyy = today.getFullYear();
    today = mm+'-'+dd+'-'+yyyy;

    mm = mm - 1;
    var monthAgo = mm+'-'+dd+'-'+yyyy;

    return(today, monthAgo);
  }

  FetchData = async() => {

    // get YT credentials from db
    const response = await fetch(keys.STRAPI_URI + '/users/me', {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + this.props.user.jwt
      },
    })
    const json = await response.json();

    if (json.youtubeaccount == null) {
      alert("Something went wrong!")
    } else {
      if (json.youtubeaccount.tokenObj.access_token != null) {
        var accessToken = json.youtubeaccount.tokenObj.access_token;
        var channelName = json.youtubeaccount.profileObj.name;
      }
      if (json.youtubeaccount.tokenObj.expires_at < new Date().getTime()) {
        alert("Uh-oh. Your access has expired, please sign-in again :)")
      }
    }

    this.GetDates();

    // GET YT Analytics API
    axios
      .get(keys.YOUTUBE_ANALYTICS_URI, {
        access_token: accessToken,
        endDate: today,
        ids: channelName,
        scope: keys.YOUTUBE_SCOPES,
        startDate: monthAgo,
        metrics: 'estimatedMinutesWatched,views,likes,subscribersGained',
        dimensions: 'hour',
        sort: 'day'
      })
      .then(response => {
        // Handle success.
        console.log('Well done!');
        console.log(response);
      })
      .catch(error => {
        // Handle error.
        console.log('An error occurred:', error);
      });

  }

  render() {

    Content = <button onClick={this.FetchData}>Fetch Data</button>

    return(
      <div className="Data">
        {Content}
      </div>
    );
  }
}

export default YouTubeData;
