import React, { Component } from 'react';
import { GoogleLogin } from 'react-google-login';

const keys = require('../../config/keys');

class YouTubeAuth extends Component {
  constructor(props) {
      super(props);
  }

  logout = () => {
      this.setState({ socialUserID: '' })
  };

  onFailure = (error) => {
    console.log(error);
