import React, { Component } from 'react';
import keys from '../config/keys';

import YouTubeAuth from './auth/YouTubeAuth';
import YouTubeData from './data/YouTubeData';

var Auth;
var Data;

class Social extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedIn: false,
      youTubeAuthorised: false
    }
  }

  componentDidUpdate = (prevProps) => {
    // Typical usage (don't forget to compare props):
    if (this.props.user.jwt !== prevProps.user.jwt) {
     this.setState({ loggedIn: !this.state.loggedIn });
    }
  }

  CheckIfValidSocialCredentialsExist = () => {

    fetch(keys.STRAPI_URI + '/users/me', {
      method: "GET",
      headers: {
            "Authorization": "Bearer " + this.props.user.jwt
        }
    })
    .then(response => response.json())
    .then(socialUser => console.log(socialUser.youtubeaccounts[0].ChannelName))
  }

  render() {

    if (this.state.loggedIn === false) {
      Auth = "^ Please login above ^";
      Data = "";
    } else {

      {this.CheckIfValidSocialCredentialsExist();}

      if (this.props.target === "YouTube") {
        Auth = <YouTubeAuth user={this.props.user}/>;
        Data = <YouTubeData user={this.props.user}/>;
      } else {
        Auth = "DEV ISSUE: Please supply a configured target";
      }
    }

    return(
      <div className="Social">
        <h2>{this.props.target}</h2>
        {Data}
        {Auth}
      </div>
    );

  }
}

export default Social;
