import React, { Component } from 'react';
import { GoogleLogin } from 'react-google-login';

const keys = require('../../config/keys');

class YouTubeAuth extends Component {
  constructor(props) {
      super(props);
  }

  logout = () => {
      this.setState({ socialUserID: '' })
  };

  onFailure = (error) => {
    console.log(error);
  };

  googleResponse = (response) => {

    let res = response;

    // Store retrieved credentials in database
    fetch(keys.STRAPI_URI + '/youtubeaccounts', {
      method: "POST",
      headers: {
        "Authorization": "Bearer " + this.props.user.jwt
      },
      body: {
        "channelName": res.profileObj.name,
        "profileObj": res.profileObj,
        "tokenObj": res.tokenObj,
        "user": this.props.user.userID
      }
    })
    .then(response => response.json())
    .then(response => console.log(response))

    // Get refresh token

  }

  render() {
    switch (this.props.youTubeAuthorised) {
      case false:
        return(
          <div>
              <GoogleLogin
                  clientId={keys.GOOGLE_CLIENT_ID}
                  scope={keys.YOUTUBE_AUTH_SCOPE}
                  buttonText="Login"
                  responseType="id_token"
                  prompt="consent"
                  accessType="offline"
                  onSuccess={this.googleResponse}
                  onFailure={this.onFailure}
              />
          </div>
        )

      default:
        return(
          <div>
          <button onClick={this.logout}>Logout</button>
          </div>
        )
    }
  }
}

export default YouTubeAuth;
