import React, { Component } from 'react';

class Social extends Component {
  constructor() {
    super();

    this.state = {
      credentials: null,
      data: null
    }
  }

  authenticate = async () => {
    console.log('authentication started');
    const res = await fetch('/auth/youtube');
    const json = await res.json();

    this.setState({ credentials: json.name });
    this.fetchData();
  }

  fetchData = async () => {
    const res = await fetch('/api/youtube/data');
    const json = await res.json();

    this.setState({ data: json.followers })
    console.log('authenticate and fetchData complete');
    console.log('credentials:' + this.state.credentials);
    console.log('data:' + this.state.data);
  }

  render() {
    switch(this.state.data) {
      case null:
        return(
          <div className="Social">
            <h2>{this.props.target}</h2>
            <button onClick={this.authenticate}>LOGIN</button>
          </div>
        );

      default:
        return(
          <div className="Social">
            <p> Name: {this.state.credentials} </p>
            <p> Followers: {this.state.data} </p>
          </div>
        );
    }
  }
}

export default Social;
