import React, { Component } from 'react';
import axios from 'axios';

const keys = require('../../config/keys');
var Content;

class YouTubeData extends Component {
  constructor(props) {
    super(props);

  }

  CheckCredentials = async() => {
    // Retrieve credentials from database
    const response = await fetch(keys.STRAPI_URI + '/users/me', {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + this.props.user.jwt
      },
    })
    const json = await response.json();
    this.isTokenValid(json)
  }

  IsTokenValid = (response) => {
    if (response.youtubeaccount == null) {
      this.setState({ authorised: false })
    } else {
      if (response.youtubeaccount.tokenObj.access_token != null) {
        this.setState({ authorised: true })
      }
      if (response.youtubeaccount.tokenObj.expires_at < new Date().getTime()) {
        this.setState({ authorised: false })
        alert("Uh-oh. Your access has expired, please sign-in again :)")
      }
    }
  }

  FetchData = async() => {

    // getting current date
    var today = new Date();
    let dd = today.getDate();
    let mm = today.getMonth()+1;
    let yyyy = today.getFullYear();
    today = mm+'-'+dd+'-'+yyyy;

    mm = mm - 1;
    var monthAgo = mm+'-'+dd+'-'+yyyy;

    // GET YT Analytics API
    axios
      .get(keys.YOUTUBE_ANALYTICS_URI, {
        access_token: this.props.user.accessToken,
        endDate: today,
        ids: this.props.channelName,
        scope: keys.YOUTUBE_SCOPES,
        startDate: monthAgo,
        metrics: 'estimatedMinutesWatched,views,likes,subscribersGained',
        dimensions: 'hour',
        sort: 'day'
      })
      .then(response => {
        // Handle success.
        console.log('Well done!');
        console.log(response);
      })
      .catch(error => {
        // Handle error.
        console.log('An error occurred:', error);
      });

  }

  render() {

    Content = <button onClick={this.FetchData}>Fetch Data</button>

    return(
      <div className="Data">
        {Content}
      </div>
    );
  }
}

export default YouTubeData;
